  CREATE OR REPLACE TRIGGER "SEYCON31"."SC_GIM_TAS_TR" 
AFTER INSERT OR DELETE ON SC_GRUIMP
FOR EACH ROW

DECLARE
	seq integer;
	codi_impres varchar2(50);
BEGIN
	select sc_tas_seq.nextval into seq from dual;
	-- cercar el codi de la impressora
	if deleting then
	   select imp_codi into codi_impres from sc_impres where imp_id=:old.gim_idimp;
	else
	   select imp_codi into codi_impres from sc_impres where imp_id=:new.gim_idimp;
	end if;
	-- actualitzar la impressora
	insert into sc_tasque
	(tas_id,tas_impres,tas_status,tas_data,tas_transa)
	values
	(seq,codi_impres,'P',sysdate,'UpdatePrinter');
END;

/
