--  DDL for Function BUSCAR_NO_IMPRIMIBLE
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "SEYCON31"."BUSCAR_NO_IMPRIMIBLE" (v_cadena VARCHAR2) 
RETURN BOOLEAN IS
  v_ret BOOLEAN := FALSE;    
  v_iter NUMBER := 1;    
  v_ascii_min NUMBER := 33;  
  v_ascii_max NUMBER := 126;
BEGIN
  WHILE (v_iter <= LENGTH(v_cadena))
    LOOP
      IF ASCII(SUBSTR(v_cadena,v_iter,1))
      NOT BETWEEN v_ascii_min AND v_ascii_max THEN
        v_ret := TRUE;
     EXIT;   
      END IF;        
      v_iter := v_iter + 1; 
    END LOOP;    
    RETURN(v_ret);
END buscar_no_imprimible;
/
 
