--------------------------------------------------------
--  DDL for Function SC_CREAR_TARGETA
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "SEYCON31"."SC_CREAR_TARGETA" (usuari IN NUMBER) RETURN VARCHAR2 IS
   CADENA VARCHAR2(10);
   ID NUMBER(10);
   i number(10);
   j number(10);
   jj varchar2(10);
   valor1 varchar2(1);
   valor2 varchar2(1);
   r number(10) := 0;
   EMISIO date;
   status varchar2(10);
   id_contar number(10);
BEGIN
   -- Obtener numero de tarjeta
   SELECT SC_TAR_SEQ.NEXTVAL INTO ID FROM DUAL;
   -- Obtener fecha de emision
   SELECT MAX(TAR_DATCAD) INTO EMISIO
   FROM SC_TARGET
   WHERE  TAR_IDUSU = USUARI AND TAR_ACTIVA IN ('S','P');
   IF EMISIO IS NULL THEN
      EMISIO := SYSDATE;
      STATUS := 'P';
   ELSE
      STATUS := 'S';
   END IF;
   CADENA := TO_CHAR(ID,'FM000000000');
   INSERT INTO SC_TARGET (TAR_ID, TAR_CODI, TAR_IDUSU, TAR_DATEMI, TAR_DATCAD, TAR_ACTIVA)
   VALUES (ID, CADENA, usuari, EMISIO, EMISIO+365,STATUS);
   for i in 0..9 loop
      for j in 1..10 loop
         jj := chr (64+j);
         r := (r + RANDOM.rndint(62)) mod 62;
         r := (r + RANDOM.rndint(62)) mod 62;
         r := (r + RANDOM.rndint(62)) mod 62;
         -- PRIMER VALOR
         loop
           r := (r + RANDOM.rndint(62)) mod 62;
           if r < 10 then valor1 := chr(48+r);
           elsif r < 36 then valor1 := chr (65-10+r);
           else valor1 := chr(97-36+r);
           end if;
           exit when (valor1 <> '-' and valor1 <> 'O' );
         end loop ;
         r := (r + RANDOM.rndint(62)) mod 62;
         -- SEGUNDO VALOR
         loop
           r := (r + RANDOM.rndint(62)) mod 62;
           if r < 10 then valor2 := chr(48+r);
           elsif r < 36 then valor2 := chr (65-10+r);
           else valor2 := chr(97-36+r);
           end if;
           exit when (valor2 <> '-' and valor2 <> 'O' );
         end loop;
         select SC_CTA_SEQ.nextval into id_contar from dual;
         insert into sc_contar (cta_idtar, cta_filcol, cta_dadaus, cta_valor, cta_id)
         VALUES (ID, JJ||TO_CHAR(I), SYSDATE, valor1||valor2, ID_CONTAR);
      end loop;
   end loop;
   return ID;
END;
/
 
--------------------------------------------------------
--  DDL for Function SC_ROLE_SUPERROLE_UPDATEABLE
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "SEYCON31"."SC_ROLE_SUPERROLE_UPDATEABLE" (ID IN NUMBER)
RETURN BOOLEAN IS
   SUPER VARCHAR2(1);
   USERID NUMBER;
   MUTATING EXCEPTION ;
   PRAGMA EXCEPTION_INIT (MUTATING, -4091);
BEGIN
   -- Verificar si es un super role que alguien tiene
   BEGIN
     SELECT 1
     INTO   SUPER
     FROM   SC_ROLUSU
     WHERE  RLU_IDROL=ID;
   EXCEPTION
   WHEN TOO_MANY_ROWS THEN NULL;
   -- EN PRE-DELETE SE PRODUCE LA EXCEPCION MUTATING
   WHEN MUTATING THEN RETURN TRUE;
   END;
   -- Verificar si el usuario lo tiene
   BEGIN
     SELECT USU_ID
     INTO   USERID
     FROM   SC_USUARI
     WHERE  USU_CODI = LOWER(USER);
     SELECT 1
     INTO   SUPER
     FROM   SC_ROLUSU
     WHERE  RLU_IDUSU=USERID AND
            RLU_IDROL=ID;
     -- Puede asignarlo porque si lo tiene
     RETURN TRUE;
   EXCEPTION
   WHEN NO_DATA_FOUND THEN
     -- No posee el role
     RETURN FALSE;
   END;
EXCEPTION
WHEN NO_DATA_FOUND THEN
   -- No es super role o nadie lo tiene
   RETURN TRUE;
END;
/
 
