      -- 3) INSERTAMOS LOR ROLES (RELACIÓN CON APLICACION-[DISPATCHER])
      -- ES IMPORTANTE QUE EN SEEDUC, EL DISPATCHER ES SIEMPRE jboss (weblogic en producción)
      DECLARE
         CURSOR C_ROLES
         IS
            SELECT   S.ROL_ID,
                     S.ROL_NOM,
                     S.ROL_DESCRI,
                     S.ROL_IDAPL,
                     S.ROL_DEFECT,
                     S.ROL_BD,
                     S.ROL_CONTRA,
                     S.ROL_SUPER
              FROM   SEEDUC.SC_ROLES@SEYCON.WORLD@SEEDUC S;

         V_ID_ROL           SC_ROLES.ROL_ID%TYPE := NULL;
         ID_DISPAT_SEEDUC   SC_DISPAT.DIS_ID%TYPE := NULL;
         -- SERA EL DE WEBLOGIC

         V_ID               NUMBER;
      BEGIN
         -- OBTENEMOS EL ID DEL DISPATCHER DE LOS ROLES (SON TODOS jboss que en prod es WEBLOGIC)
         SELECT   DIS_ID
           INTO   ID_DISPAT_SEEDUC
           FROM   SC_DISPAT
          WHERE   DIS_CODI = 'weblogic';

         -- NOTA: LO ANTERIOR TIENE QUE DEVOLVERNOS ALGO EL DISPATCHER, SI NO NO CONTINUAMOS
         IF ID_DISPAT_SEEDUC IS NULL
         THEN
            RAISE_APPLICATION_ERROR (
               -20001,
               'NO SE HA ENCONTRADO EL DISPATCHER weblogic, no se puede continuar'
            );
         END IF;


         FOR ROLS IN C_ROLES
         LOOP
            -- ¿EXISTE YA EL NOMBRE, IDAPL, IDDISPAT? OBTENEMOS EL ID ACTUAL SI ES ASÍ
            -- LA CLAVE ÚNICA ES (NOM, IDAPL, IDDISPAT)
            BEGIN
               SELECT   ROL_ID
                 INTO   V_ID_ROL
                 FROM      SC_ROLES R
                        LEFT JOIN
                           TMP_SEEDUC APLICA                 -- PUEDE SER NULO
                        ON APLICA.TAULA = 'SC_APLICA'
                           AND APLICA.IDANTIC = ROLS.ROL_IDAPL
                -- COMPROBAMOS LA CLAVE ÚNICA
                WHERE       R.ROL_NOM = ROLS.ROL_NOM
                        AND R.ROL_IDAPL = APLICA.IDNOU
                        AND R.ROL_IDDISPAT = ID_DISPAT_SEEDUC;
            -- WEBLOGIC EN PROD
            -- TODOS SON jboss (weblogic)
            EXCEPTION
               WHEN OTHERS
               THEN
                  V_ID_ROL := NULL;
            END;

            IF V_ID_ROL IS NULL                     -- ES NUEVO: LO INSERTAMOS
            THEN
               -- GENERAMOS EL ID NUEVO
               SELECT   HIBERNATE_SEQUENCE.NEXTVAL INTO V_ID FROM DUAL;

               -- INSERTAMOS CON EL ID NUEVO, Y LAS CORRESPONDENCIAS
               -- CON LA APLICACION Y EL DISPATCHER (SIEMPRE WEBLOGIC EN PROD)
               INSERT INTO SEYCON31.SC_ROLES (ROL_ID,
                                              ROL_NOM,
                                              ROL_DESCRI,
                                              ROL_IDAPL,
                                              ROL_DEFECT,
                                              ROL_CONTRA,
                                              ROL_IDDISPAT,
                                              ROL_DOMAPP,
                                              ROL_TIPDOM,
                                              ROL_BD)
                  SELECT   V_ID ROL_ID,
                           ROLS.ROL_NOM,
                           ROLS.ROL_DESCRI,
                           APLICA.IDNOU ROL_IDAPL,           -- FK A SC_APLICA
                           ROLS.ROL_DEFECT,
                           ROLS.ROL_CONTRA,
                           id_dispat_seeduc ROL_IDDISPAT,
                           -- AQUI ES SIEMPRE EL MISMO (jboss- weblogic en producción)
                           -- FK A SC_DISPAT (REEMPLAZA A ROL_BD QUE YA NO SE USA)
                           NULL ROL_DOMAPP,
                           NULL ROL_TIPDOM,
                           NULL ROL_BD         -- no ponemos nada (es varchar)
                    FROM      DUAL
                           LEFT JOIN                        -- PUEDE? SER NULO
                              TMP_SEEDUC APLICA
                           ON APLICA.TAULA = 'SC_APLICA'
                              AND APLICA.IDANTIC = ROLS.ROL_IDAPL;

               -- GUARDAMOS LOS IDS EN LA TABLA TEMPORAL
               INSERT INTO SEYCON31.TMP_SEEDUC (IDNOU,
                                                IDANTIC,
                                                TAULA,
                                                NOU)
                 VALUES   (V_ID,
                           ROLS.ROL_ID,
                           'SC_ROLES',
                           'S');
            ELSE                                    -- INSERTAMOS EL ID ACTUAL
               INSERT INTO SEYCON31.TMP_SEEDUC (IDNOU,
                                                IDANTIC,
                                                TAULA,
                                                NOU)
                 VALUES   (V_ID_ROL,
                           ROLS.ROL_ID,
                           'SC_ROLES',
                           'N');
            END IF;
         END LOOP;
      END;


