            IF V_ID_DADUSU IS NULL                  -- ES NUEVO: LO INSERTAMOS
            THEN
               -- GENERAMOS EL ID NUEVO
               SELECT   HIBERNATE_SEQUENCE.NEXTVAL INTO V_ID FROM DUAL;

               INSERT INTO SEYCON31.SC_DADUSU (DUS_IDUSU,
                                               DUS_VALOR,
