 PROCEDURE split (alias in varchar2, shortname out varchar2, domain out number) IS
  i number(3);
  subdomini varchar2(1024);
 BEGIN
  i := instr (alias,'@');
  if i = 0 then i := instr (alias,'.'); end if;

  if i > 0 then
     shortname := substr(alias,1,i-1);
     subdomini := substr (alias, i+1);
     if subdomini = 'caib.es' then
        subdomini := null;
        domain := null;
     else
      if substr (subdomini,length(subdomini)-7) = '.caib.es' then
        subdomini := substr (subdomini,1,length(subdomini)-8);
      end if;
      BEGIN
       SELECT DCO_ID INTO domain
       FROM   SC_DOMCOR
       WHERE  DCO_CODI = subdomini;
      EXCEPTION
      WHEN NO_DATA_FOUND THEN
       domain := null;
       shortname := alias;
      END;
     end if;
  else
     shortname := alias;
     domain := null;
  end if;
 END;


 PROCEDURE insertLcoMember (id in number, member in varchar2) IS
  i number(3);
  codi varchar2(1024);
  subdomini varchar2(1024);
  iddco number(10);
  alert_button number;
 BEGIN
  if member is null or length (member) = 0 then
    return;
  end if;
  i := instr (member,'@');
  if i = 0 then i := instr (member,'.'); end if;

  if i > 0 then
     subdomini := substr (member, i+1);
     codi := substr(member,1,i-1);
     if subdomini = 'caib.es' then
        subdomini := null;
        iddco := null;
     else
      if substr (subdomini,length(subdomini)-7) = '.caib.es' then
        subdomini := substr (subdomini,1,length(subdomini)-8);

      end if;
      BEGIN
       SELECT DCO_ID INTO iddco
       FROM   SC_DOMCOR
       WHERE  DCO_CODI = subdomini;
      EXCEPTION
      WHEN NO_DATA_FOUND THEN
       iddco := null;
       codi := member;
      END;
     end if ;
  else
     codi := member;
     subdomini := null;
     iddco := null;
  end if;

  -- Buscar por codigo de usuario
  IF iddco is null THEN
     DECLARE
       idusu number(10);
     BEGIN
       SELECT USU_ID INTO idusu
       FROM   SC_USUARI
