 PROCEDURE saveLcoMembers (id in number, members in varchar2) IS
  i number(5);
  x varchar2(512);

 BEGIN
  DELETE SC_LCOLCO WHERE LLC_IDLCO1 = id;
  DELETE SC_USULCO WHERE ULC_IDLCO = id;
  DELETE SC_EXTLCO WHERE ELC_IDLCO = id;

  x := '';
  i := 1;
  WHILE i <= length(members) LOOP
    IF SUBSTR(members,i,1) = ',' OR
       SUBSTR(members,i,1) = ' ' THEN
       insertLcoMember (id, x);
       x := '';
    ELSE
       x := x || substr (members,i,1);
    END IF;
    i := i + 1;
  END LOOP;
  insertLcoMember (id, x);

 END;

  PROCEDURE NEWLCO (LCO IN VARCHAR2, MEMBERS IN VARCHAR2)
  IS
     X NUMBER(10);
     shortname varchar2(25);
     iddco number(10);
  BEGIN
     split (lco, shortname, iddco);
     BEGIN
        SELECT LCO_ID INTO  X FROM SC_LLICOR WHERE LCO_NOM=shortname AND NVL(LCO_IDDCO,'-1')=NVL(iddco,-1);
     EXCEPTION
     WHEN NO_DATA_FOUND THEN
       SELECT SC_LCO_SEQ.NEXTVAL INTO X FROM DUAL;
       INSERT INTO SC_LLICOR (LCO_ID, LCO_NOM, LCO_DESCRI, LCO_IDDCO)
       VALUES (X, shortname, 'Importada NIS+', iddco);
     END;
     SaveLcoMembers (X, MEMBERS);
  END;
END;
