  CREATE OR REPLACE PROCEDURE "SEYCON31"."SELECT_CRIS" (p_cursor OUT SYS_REFCURSOR)
IS
codi varchar2(10);
nom varchar2(128);
BEGIN
OPEN p_cursor FOR
select pue_codi,pue_nom into codi,nom from sc_punent where pue_codi='ETIQFISC';
END;
