--  DDL for Procedure SC_UPDATE_USER
--------------------------------------------------------

  CREATE OR REPLACE PROCEDURE "SEYCON31"."SC_UPDATE_USER" (usuari varchar2) IS
BEGIN
   UPDATE SC_CONTRA
   SET    CTR_ACTIU='N'
   WHERE  CTR_ORDRE=0 AND CTR_IDUSU IN (
          SELECT USU_ID FROM SC_USUARI WHERE USU_CODI=usuari);
   INSERT INTO SC_TASQUE (TAS_ID, TAS_TRANSA, TAS_DATA, TAS_USUARI)
   VALUES (SC_TAS_SEQ.NEXTVAL, 'UpdateUser', SYSDATE,usuari);
END;
